-- ----------------------------
-- Table structure for `article`
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `images` varchar(255) DEFAULT NULL COMMENT '文章中图片',
  `content` text COMMENT '内容',
  `source_id` varchar(255) DEFAULT NULL COMMENT '来源',
  `users_id` int(11) DEFAULT NULL COMMENT '用户id',
  `author` varchar(255) DEFAULT NULL COMMENT '作者',
  `keyword` varchar(255) DEFAULT NULL COMMENT '关键字',
  `category_id` smallint(6) DEFAULT NULL COMMENT '分类',
  `pageviews` bigint(20) DEFAULT '0' COMMENT '页面阅读量',
  `status` smallint(6) NOT NULL DEFAULT '1' COMMENT '状态：1正常2采集3临时采集4回收站',
  `collecturl` varchar(255) DEFAULT NULL COMMENT '采集网址',
  `look` int(11) DEFAULT '0' COMMENT '浏览量',
  `created_at` datetime DEFAULT NULL COMMENT '创建时间',
  `updated_at` datetime DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `title` (`title`(191)),
  KEY `collecturl` (`collecturl`(191))
) ENGINE=InnoDB AUTO_INCREMENT=339 DEFAULT CHARSET=utf8mb4 COMMENT='文章';

